    }

    .episode-list h4 {
        font-size: 1.3rem;
        margin-bottom: 15px;
        padding-bottom: 10px;
        border-bottom: 2px solid #f0f0f0;
        color: var(--dark);
    }

    .episodes {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
        gap: 12px;
    }

    .episode {
        background: #f0f0f0;
        padding: 12px;
        text-align: center;
        border-radius: 8px;
        cursor: pointer;
        transition: all 0.2s ease;
        font-weight: 500;
    }

    .episode:hover {
        background: var(--primary);
        color: white;
        transform: translateY(-3px);
        box-shadow: 0 5px 15px rgba(138, 43, 226, 0.3);
    }

    .empty-state {
        text-align: center;
        padding: 50px 25px; /* å¢åŠ å·¦å³å†…è¾¹è· */
        background: white;
        border-radius: 15px;
        box-shadow: var(--card-shadow);
        grid-column: 1 / -1; /* å…³é”®ä¿®æ”¹ï¼šä½¿ç©ºçŠ¶æ€è·¨è¶Šæ‰€æœ‰åˆ— */
    }

    .empty-state i {
        font-size: 4rem;
        color: #e0e0e0;
        margin-bottom: 20px;
    }

    .empty-state h3 {
        font-size: 1.8rem;
        margin-bottom: 15px;
        color: var(--dark);
    }

    .empty-state p {
        font-size: 1.1rem;
        color: var(--gray);
        max-width: 600px;
        margin: 0 auto;
        line-height: 1.8;
    }

    .notification {
        position: fixed;
        top: 20px;
        right: 20px;
        padding: 15px 25px;
        border-radius: 10px;
        background: var(--primary);
        color: white;
        box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
