    // è·å–åŠ¨æ¼«æ•°æ® - ä¿®å¤ç‰ˆ
    async function fetchAnimeData() {
        const animeName = document.getElementById('animeName').value.trim();
        if (!animeName) {
            showNotification('è¯·è¾“å…¥åŠ¨æ¼«åç§°', true);
            return;
        }

        currentAnimeName = animeName;
        currentLineType = document.getElementById('lineType').value;

        showLoading('æ­£åœ¨æœç´¢åŠ¨æ¼«...');

        try {
            const url = 'https://oiapi.net/API/Anime';
            const params = new URLSearchParams({
                msg: animeName,
                n: 0,
                j: 0,
                type: currentLineType
            });

            // æ·»åŠ æ—¶é—´æˆ³é˜²æ­¢ç¼“å­˜
            const fullUrl = `${url}?${params}&t=${Date.now()}`;

            // ä½¿ç”¨CORSä»£ç†è§£å†³è·¨åŸŸé—®é¢˜
            const proxyUrl = `https://api.allorigins.win/get?url=${encodeURIComponent(fullUrl)}`;

            const response = await fetch(proxyUrl);

            // è§£æä»£ç†å“åº”
            const proxyData = await response.json();

            // æ£€æŸ¥ä»£ç†å“åº”æ˜¯å¦åŒ…å«åŸå§‹å†…å®¹
            if (!proxyData.contents) {
                throw new Error('ä»£ç†æœåŠ¡è¿”å›æ— æ•ˆæ•°æ®');
            }

            // è§£æåŸå§‹APIå“åº”
            const data = JSON.parse(proxyData.contents);

            // æ–°å¢ï¼šæ‰“å°æœç´¢ç»“æœçš„å®Œæ•´JSONæ•°æ®
            console.log("[æœç´¢åŠ¨æ¼«åˆ—è¡¨] è¿”å›çš„å®Œæ•´JSONæ•°æ®:", data);

            if (data.code === 200) {
                displayAnimeList(data.data);
                showNotification(`æ‰¾åˆ° ${data.data.length} éƒ¨ç›¸å…³åŠ¨æ¼«`);
            } else {
                showNotification(data.msg || 'æŸ¥è¯¢å¤±è´¥ï¼Œè¯·é‡è¯•', true);
            }
        } catch (error) {
            console.error('è¯·æ±‚å¼‚å¸¸:', error);
            showNotification(`è¯·æ±‚å¼‚å¸¸: ${error.message}`, true);
        } finally {
            hideLoading();
        }
    }

    // æ˜¾ç¤ºåŠ¨æ¼«åˆ—è¡¨
    function displayAnimeList(animeList) {
        const resultsArea = document.getElementById('resultsArea');
        resultsArea.innerHTML = '';

        if (!animeList || animeList.length === 0) {
            resultsArea.innerHTML = `


                    æœªæ‰¾åˆ°ç›¸å…³åŠ¨æ¼«
                    æ²¡æœ‰æ‰¾åˆ°ä¸"${currentAnimeName}"ç›¸å…³çš„åŠ¨æ¼«ï¼Œè¯·å°è¯•å…¶ä»–å…³é”®è¯

            `;
            return;
        }

        animeList.forEach((anime, index) => {
            const animeCard = document.createElement('div');
            animeCard.className = 'anime-card';
            animeCard.innerHTML = `


                    ${anime.name}

                        ${anime.year || 'æœªçŸ¥å¹´ä»½'}
                        ${anime.ji || 'çŠ¶æ€æœªçŸ¥'}









            `;
            animeCard.onclick = () => showAnimeDetails(index + 1);
            resultsArea.appendChild(animeCard);
