    }

    // æ˜¾ç¤ºåŠ¨æ¼«è¯¦æƒ…
    async function showAnimeDetails(nIndex) {
        currentNIndex = nIndex;
        showLoading('æ­£åœ¨åŠ è½½åŠ¨æ¼«è¯¦æƒ…...');

        try {
            const url = 'https://oiapi.net/API/Anime';
            const params = new URLSearchParams({
                msg: currentAnimeName,
                n: nIndex,
                j: 0,
                type: currentLineType
            });

            // æ·»åŠ æ—¶é—´æˆ³é˜²æ­¢ç¼“å­˜
            const fullUrl = `${url}?${params}&t=${Date.now()}`;

            // ä½¿ç”¨CORSä»£ç†è§£å†³è·¨åŸŸé—®é¢˜
            const proxyUrl = `https://api.allorigins.win/get?url=${encodeURIComponent(fullUrl)}`;

            const response = await fetch(proxyUrl);

            // è§£æä»£ç†å“åº”
            const proxyData = await response.json();

            // æ£€æŸ¥ä»£ç†å“åº”æ˜¯å¦åŒ…å«åŸå§‹å†…å®¹
            if (!proxyData.contents) {
                throw new Error('ä»£ç†æœåŠ¡è¿”å›æ— æ•ˆæ•°æ®');
            }

            // è§£æåŸå§‹APIå“åº”
            const data = JSON.parse(proxyData.contents);

            // æ–°å¢ï¼šæ‰“å°åŠ¨æ¼«è¯¦æƒ…çš„å®Œæ•´JSONæ•°æ®
            console.log(`[åŠ¨æ¼«è¯¦æƒ…] ${currentAnimeName} çš„å®Œæ•´JSONæ•°æ®:`, data);

            if (data.code === 200) {
                openAnimeDetailWindow(data.data, nIndex);
            } else {
                showNotification(data.msg || 'è·å–è¯¦æƒ…å¤±è´¥', true);
            }
        } catch (error) {
            console.error('è¯·æ±‚å¼‚å¸¸:', error);
