function handleFileSelect(event) {
    const file = event.target.files[0];
    if (file) {
        const reader = new FileReader();
        reader.onload = function(e) {
            // ç§»é™¤ä¹‹å‰çš„å›¾åƒ
            if (img) {
                URL.revokeObjectURL(img.src);
            }

            img = new Image();
            img.src = e.target.result;
            img.onload = function() {
                const canvas = document.getElementById('cardCanvas');
                const ctx = canvas.getContext('2d');
                ctx.clearRect(0, 0, canvas.width, canvas.height);

                // è®¡ç®—å›¾åƒçš„å®½é«˜æ¯”
                const aspectRatio = img.width / img.height;

                // ç¡®å®šæ˜¯å°†å›¾åƒçš„å®½åº¦è¿˜æ˜¯é«˜åº¦é€‚åº”åˆ°ç”»å¸ƒ
                if (img.width / canvas.width < img.height / canvas.height) {
                    imgProps.scale = canvas.width / img.width;
                } else {
                    imgProps.scale = canvas.height / img.height;
                }

                // è®¾ç½®åŸå§‹ç¼©æ”¾æ¯”ä¾‹ä¸ºå½“å‰ç¼©æ”¾æ¯”ä¾‹
                imgProps.originalScale = imgProps.scale;

                // é‡ç½®åç§»é‡
                imgProps.offsetX = 0;
                imgProps.offsetY = 0;

                // æ›´æ–°è¾“å…¥æ¡†çš„å€¼ä¸ºåˆå§‹ç¼©æ”¾æ¯”ä¾‹
                scaleInput.value = (imgProps.scale * 100).toFixed(2);

                // åœ¨ç”»å¸ƒä¸Šç»˜åˆ¶å›¾åƒ
                redraw();
            };
        };
        reader.readAsDataURL(file);
    }
}

