        this.style.backgroundColor = '#0056b3'; // æ”¹å˜èƒŒæ™¯é¢œè‰²ä¸ºæ‚¬åœçŠ¶æ€
    });

    button.addEventListener('mouseleave', function() {
        this.style.backgroundColor = '#007bff'; // æ¢å¤é»˜è®¤èƒŒæ™¯é¢œè‰²
    });

    button.addEventListener('mousedown', function() {
        this.style.backgroundColor = '#0056b3'; // æ”¹å˜èƒŒæ™¯é¢œè‰²ä¸ºç‚¹å‡»çŠ¶æ€
    });

    button.addEventListener('mouseup', function() {
        this.style.backgroundColor = '#007bff'; // æ¢å¤é»˜è®¤èƒŒæ™¯é¢œè‰²
    });
});

// ä¸º GitHub å›¾æ ‡é“¾æ¥æ·»åŠ é¼ æ ‡æ‚¬åœå’Œç‚¹å‡»äº‹ä»¶ç›‘å¬å™¨
githubLink.addEventListener('mouseover', function() {
