        background: radial-gradient(ellipse at bottom, #1b2735 0%, #090a0f 100%);
        overflow: hidden;
        font-family: 'Segoe UI', 'Microsoft YaHei', sans-serif;
        color: white;
        text-align: center;
        display: flex;
        flex-direction: column;
        justify-content: center;
        position: relative;
    }

    h1 {
        margin: 20px 0;
        font-size: 2.5rem;
        text-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
    }

    p {
        font-size: 1.2rem;
        margin: 10px 0;
        line-height: 1.6;
    }

    .shooting-star {
        position: absolute;
        width: 2px;
        height: 80px;
        background: linear-gradient(-45deg, white, rgba(255,255,255,0));
        animation: tail 1.5s ease-out infinite;
        filter: drop-shadow(0 0 5px white);
    }

    @keyframes tail {
        0% {
            transform: translateX(-300px) translateY(-300px) rotate(-45deg);
            opacity: 1;
        }
        100% {
            transform: translateX(300px) translateY(300px) rotate(-45deg);
            opacity: 0;
        }
    }

    .starfield {
        position: absolute;
        width: 100%;
        height: 100%;
        overflow: hidden;
        z-index: 0;
    }

    .content {
        position: relative;
        z-index: 1;
        padding: 20px;
        max-width: 800px;
        margin: 0 auto;
    }

    .twinkle-star {
        position: absolute;
        width: 3px;
        height: 3px;
        background: rgba(255,255,255,0.9);
        border-radius: 50%;
        animation: twinkle 2s infinite;
        box-shadow: 0 0 10px rgba(255,255,255,0.5);
    }

    @keyframes twinkle {
        0%, 100% { 
            opacity: 0.3; 
            transform: scale(1);
        }
        50% { 
            opacity: 1;
            transform: scale(1.5);
            background: hsl(var(--hue), 100%, 70%);
        }
    }

    .countdown-container {
        background: rgba(0,0,0,0.3);
        padding: 25px;
        border-radius: 12px;
        margin: 30px auto;
        width: 90%;
        max-width: 500px;
        backdrop-filter: blur(5px);
        border: 1px solid rgba(255,255,255,0.1);
    }

    .countdown-title {
        font-size: 1.2rem;
        margin-bottom: 15px;
    }

    #countdown {
        color: #FFD700;
        font-size: 2.5rem;
        font-weight: bold;
        filter: drop-shadow(0 0 5px #FFD700);
        margin: 10px 0;
    }

    .time-info {
        margin-top: 20px;
        font-size: 0.9rem;
        color: rgba(255,255,255,0.8);
    }

    .time-info div {
        margin: 5px 0;
    }

    .audio-toggle {
        position: fixed;
        bottom: 20px;
        left: 50%;
        transform: translateX(-50%);
        z-index: 10;
        background: rgba(0,0,0,0.7);
        border-radius: 50%;
        width: 40px;
        height: 40px;
        display: flex;
        align-items: center;
        justify-content: center;
        cursor: pointer;
        transition: all 0.3s ease;
    }

    .audio-toggle:hover {
        background: rgba(0,0,0,0.9);
        width: 200px;
        border-radius: 20px;
    }

    .audio-toggle:hover .audio-player {
        opacity: 1;
        width: 180px;
    }

    .audio-player {
        opacity: 0;
        width: 0;
        transition: all 0.3s ease;
        overflow: hidden;
    }

    audio {
        width: 100%;
        max-width: 180px;
    }

    .audio-icon {
        font-size: 20px;
        position: absolute;
    }

    .audio-toggle:hover .audio-icon {
        opacity: 0;
    }

    .emoji {
        font-size: 1.5em;
        vertical-align: middle;
    }








