    function updateTime() {
        const target = new Date('2025-04-28T13:30:00+08:00');
        const now = new Date();

        // å½“å‰æ—¶é—´
        document.getElementById('current-time').textContent =
            `å½“å‰æ—¶é—´ï¼š${now.toLocaleString('zh-CN', {
                year: 'numeric',
