let chapters = [];
let currentChapter = 0;
let splitPattern = /\n\n/;
let bookmarksMap = {}; // æ–°å¢ï¼šä»¥æ–‡ä»¶åä¸ºkeyçš„ä¹¦ç­¾æ˜ å°„
