    // æ¸¸æˆå¾ªç¯
    function gameLoop(timestamp) {
        // è®¡ç®—æ—¶é—´å·®
        if (lastTime === 0) lastTime = timestamp;
        deltaTime = timestamp - lastTime;
        lastTime = timestamp;
        accumulatedTime += deltaTime;

        // ç§»åŠ¨ç«¯æ€§èƒ½ä¼˜åŒ–ï¼šé™ä½å¸§ç‡
        const targetFPS = isMobileDevice() ? 60 : 120;
        const targetFrameInterval = 1000 / targetFPS;

        // æ›´æ–°FPSè®¡æ•°
        frameCount++;
        if (timestamp - lastFpsUpdate >= 1000) {
            const fps = Math.round(frameCount * 1000 / (timestamp - lastFpsUpdate));
            console.log(`FPS: ${fps}`);
            frameCount = 0;
            lastFpsUpdate = timestamp;
        }

        // æ‰§è¡Œå›ºå®šæ¬¡æ•°çš„æ›´æ–°ï¼ˆæ—¶é—´è¡¥å¿ï¼‰
        while (accumulatedTime >= targetFrameInterval) {
            update(targetFrameInterval);
            accumulatedTime -= targetFrameInterval;
        }
        draw();
        requestAnimationFrame(gameLoop);
    }

    // å¯åŠ¨æ¸¸æˆ
    lastTime = 0;
    accumulatedTime = 0;
    requestAnimationFrame(gameLoop);

    // ====== ç§»åŠ¨ç«¯è§¦æ‘¸äº‹ä»¶æ”¯æŒ ======
    canvas.addEventListener('touchstart', handleTouch, {passive: false});
    canvas.addEventListener('touchmove', handleTouch, {passive: false});
    function handleTouch(e) {
        if (isGameOver || isPaused) return;
        e.preventDefault();
        const rect = canvas.getBoundingClientRect();
        let clientY;
        if (e.touches && e.touches.length > 0) {
            clientY = e.touches[0].clientY;
        } else {
            clientY = e.clientY;
        }
        player.y = clientY - rect.top - player.height / 2;
        // é™åˆ¶æŒ¡æ¿ä¸å‡ºç•Œ
        if (player.y < 0) player.y = 0;
        if (player.y + player.height > canvas.height) player.y = canvas.height - player.height;
    }

    // ====== canvasè‡ªé€‚åº”å±å¹• ======
    function resizeCanvas() {
        let w, h;

        if (isMobileDevice()) {
            // ç§»åŠ¨ç«¯ï¼šä½¿ç”¨å±å¹•å®½åº¦çš„95%ï¼Œé«˜åº¦ä¸ºå±å¹•é«˜åº¦çš„50%
            w = window.innerWidth * 0.95;
            h = window.innerHeight * 0.5;
