// æ›´æ–°å›¾åƒå¤§å°çš„å‡½æ•°
function updateScale() {
    const newScale = parseFloat(scaleInput.value) / 100; // è·å–è¾“å…¥æ¡†çš„å€¼å¹¶è½¬æ¢ä¸ºå°æ•°å½¢å¼
    const initialScale = imgProps.originalScale; // è·å–åˆå§‹è®°å½•çš„ç¼©æ”¾æ¯”ä¾‹

    // å¦‚æœè¾“å…¥çš„ç¼©æ”¾æ¯”ä¾‹å°äºåˆå§‹è®°å½•çš„å€¼ï¼Œåˆ™å°†è¾“å…¥çš„å€¼è°ƒæ•´ä¸ºåˆå§‹è®°å½•çš„å€¼
    if (newScale < initialScale) {
        scaleInput.value = (initialScale * 100).toFixed(2); // å°†è¾“å…¥æ¡†çš„å€¼è®¾ä¸ºåˆå§‹è®°å½•çš„å€¼

        // åˆ›å»ºæç¤ºæ¡†å…ƒç´ 
        const alertBox = document.createElement('div');
        alertBox.textContent = 'å›¾ç‰‡ä¸å¾—è¿‡å°';
        alertBox.classList.add('alert-box');
        document.body.appendChild(alertBox);

        // åœ¨ä¸‹ä¸€ä¸ªåŠ¨ç”»å¸§ä¸­æ·»åŠ æ·¡å‡ºæ•ˆæœ
        requestAnimationFrame(() => {
            alertBox.style.opacity = '1'; // æ·¡å…¥æ•ˆæœ
        });

        // è®¾ç½®å®šæ—¶å™¨ï¼Œåœ¨3ç§’åéšè—æç¤ºæ¡†
        setTimeout(function() {
            // åœ¨ä¸‹ä¸€ä¸ªåŠ¨ç”»å¸§ä¸­æ·»åŠ æ·¡å‡ºæ•ˆæœ
            requestAnimationFrame(() => {
                alertBox.style.opacity = '0'; // æ·¡å‡ºæ•ˆæœ
            });
            // 3ç§’ååˆ é™¤æç¤ºæ¡†
            setTimeout(() => {
                document.body.removeChild(alertBox);
            }, 300);
        }, 3000);

        imgProps.scale = initialScale;
        imgProps.offsetX = 1;
        imgProps.offsetY *= 1;

        // é™åˆ¶åç§»é‡ä»¥ç¡®ä¿å›¾åƒä¿æŒåœ¨ç”»å¸ƒèŒƒå›´å†…
        limitOffset();

        // é‡æ–°ç»˜åˆ¶å›¾åƒ
        redraw();

        return; // ç»“æŸå‡½æ•°ï¼Œä¸è¿›è¡Œåç»­çš„æ“ä½œ
    }

    const scaleDiff = newScale / imgProps.scale; // è®¡ç®—ç¼©æ”¾æ¯”ä¾‹å˜åŒ–

    // æ›´æ–°å›¾åƒçš„ç¼©æ”¾æ¯”ä¾‹å’Œåç§»é‡
    imgProps.scale = newScale;
    imgProps.offsetX *= scaleDiff;
    imgProps.offsetY *= scaleDiff;

    // é™åˆ¶åç§»é‡ä»¥ç¡®ä¿å›¾åƒä¿æŒåœ¨ç”»å¸ƒèŒƒå›´å†…
    limitOffset();

    // é‡æ–°ç»˜åˆ¶å›¾åƒ
    redraw();
}

