function flipImage() {
    if (img) {
        const canvas = document.getElementById('cardCanvas');
        const ctx = canvas.getContext('2d');

        // æ¸…é™¤ç”»å¸ƒ
        ctx.clearRect(0, 0, canvas.width, canvas.height);

        // å°†å›¾ç‰‡æ°´å¹³ç¿»è½¬
        ctx.translate(canvas.width, 0);
        ctx.scale(-1, 1);

        // é‡æ–°ç»˜åˆ¶å›¾åƒ
        redraw();
    }
}

